scriptlet.script:
// This template shows how to access operation return data, inputs and context data 
// and how to set context data, responses and results. 

// To access an input simply refer to the input name from the Inputs panel.  For example: 
date = nextDraftDate;
newdate = new Date(date);

newdate.setDate(newdate.getDate() + 1);

day = newdate.getDay();    
dd = newdate.getDate();
mm = newdate.getMonth() + 1;
y = newdate.getFullYear();


addedDate = mm + '/' + dd + '/' + y;
date=addedDate

if(day==0){
    newdate = new Date(date);
    newdate.setDate(newdate.getDate() + 1);
    addedDate=newdate.getMonth() + 1 + '/' + newdate.getDate() + '/' + newdate.getFullYear();
}
else if(day==6){
    newdate = new Date(date);
    newdate.setDate(newdate.getDate() + 2);
    addedDate=newdate.getMonth() + 1 + '/' + newdate.getDate() + '/' + newdate.getFullYear();
} 



scriptletContext.put("actionByDate",addedDate);


scriptletResult = addedDate;

