staticBinding.value:
SELECT TOP ${limit}
    PSW_SLNO as slno ,PSW_emailSubject 
FROM ${tableName} 
WHERE 
    PSW_NextTouchDate <= CURRENT_TIMESTAMP AND 
    PSW_flagAsDay = 'Day0'

scriptlet.script:
try{
    var rowsList = rows.split(rowDelimiter);
    //var rowsList = "1;sub1|2;sub2|3;sub3".split("|");
    var output = '';
    var response = '';
    threads = parseInt(threads);
    //threads = parseInt('1');

    id_list = [];
    subject_list = [];

    ids = [];
    subjects = [];

    for (var i = 0; i < rowsList.length; i++){
        id_list.push(rowsList[i].split(';')[0]);
        subject_list.push(rowsList[i].split(';')[1])
    }

    for (var i = 0; i < id_list.length; i=i+threads){
        //output += rowsList.slice(i, i + threads) + "|";
        ids += id_list.slice(i, i + threads) + "|";
        subjects += subject_list.slice(i, i + threads) + "|";
        output += id_list.slice(i, i + threads) + "::" + subject_list.slice(i, i + threads) + "|";
        //1::sub1|2::sub2|3::sub3
    }

    scriptletContext.put("processableRequests", output.slice(0, -1));             
    response = 'success';
} catch(err) {
    scriptletContext.put("errorMessage", err.message);      
    response = 'failure';
}
scriptletResponse = response;
