scriptlet.script:
if (scriptletInput !== null && scriptletInput != '') {
  scriptletResult = scriptletInput.split(',').length;
} else {
	scriptletResult = '';
}

if (scriptletInput !== null) {
	//replace '.' with '_<unix timstamp>.' in file name to generate unique filename  
	fileName = scriptletInput.replace('.', '_' + new Date().getTime() + '.');
  	scriptletResult = fileName
}
